import React, {useEffect, useState} from "react";
import axios from "axios";
import './CreateAnswer.css'
import {ENDPOINT_GET_BOARDS} from "../../../../common/Endpoint/Endpoint";

function BoardRecord({board, setBoardId}) {
    return (
        <tr>
            <td>{board.title}</td>
            <td>{board.author}</td>
            <td>{board.content}</td>
            <button onClick={e => setBoardId(board.id)}>답변하기</button>
        </tr>
    );
}

export default function CreateAnswer({employee}) {

    const [boards, setBoards] = useState(null);

    useEffect(() => {
        axios.get(ENDPOINT_GET_BOARDS)
            .then(response => {
                setBoards(response.data);
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    }, []);

    function handleSubmit(e) {
        e.preventDefault();
        const boardData = {
            id: boardId,
            answer: content,
            answerer: employee.id
        };
        axios.post('http://localhost:40022/api/board/update', boardData)
            .then(response => {
                alert('답변이 작성되었습니다.')
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
        setBoardId(null);
        axios.get('http://localhost:40022/api/boards')
            .then(response => {
                setBoards(response.data);
                console.log(response.data)
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    }

    let listItems = null;
    if (boards !== null) {
        listItems = boards.filter(board =>
            board.answerer === ''
        ).map(board => <BoardRecord board={board} setBoardId={setBoardId}/>);
    } else listItems = "loading...";

    if (boardId == null) {
        return (<>
            <div className={'board'}>
                <h1>문의 게시판</h1>
                <table>
                    <tr>
                        <th>
                            제목
                        </th>
                        <th>
                            작성자
                        </th>
                        <th>
                            내용
                        </th>
                        <th>
                            답변 버튼
                        </th>
                    </tr>
                    {listItems}
                </table>
            </div>
        </>);
    } else {
        return (
            <>
                <form onSubmit={handleSubmit}>
                    <div className="form">
                        <label htmlFor="content">답변</label>
                        <input type="content" id="content" name="content" value={content}
                               onChange={(e) => setContent(e.target.value)} required/>
                    </div>
                    <button type="submit">작성 완료</button>
                </form>
            </>
        );
    }
}
