import React, {useEffect, useState} from "react";
import axios from "axios";
import './ViewProducts.css'

export default function ViewProducts({employee}) {

    const [productList, setProductList] = useState(null);

    useEffect(() => {
            axios.get('http://localhost:40055/api/products')
                .then(response => {
                    setProductList(response.data);
                    console.log(response.data)
                })
                .catch(error => {
                    alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
                });
        },
        []
    );

    let listItems = null;
    if (productList !== null) {
        listItems = productList.map(product => <tr>
            <td>
                {product.id}
            </td>
            <td>
                {product.name}
            </td>
            <td>
                {product.premium}
            </td>
            <td>
                {product.seniorRate}
            </td>
            <td>
                {product.maleRate}
            </td>
            <td>
                {product.femaleRate}
            </td>
            <td>
                {product.occupationHazardRate}
            </td>
            <td>
                {product.smokingRate}
            </td>
            <td>
                {product.released === 0 ? '아니오' : '네'}
            </td>
        </tr>);
    } else listItems = "loading...";

    return (<>
        <div className={'전체 상품 조회'}>
            <h1>전체 상품 조회</h1>
            <table>
                <tr>
                    <th>
                        아이디
                    </th>
                    <th>
                        이름
                    </th>
                    <th>
                        보험료
                    </th>
                    <th>
                        고령 요율
                    </th>
                    <th>
                        남성 요율
                    </th>
                    <th>
                        여성 요율
                    </th>
                    <th>
                        위험 직업 요율
                    </th>
                    <th>
                        흡연 요율
                    </th>
                    <th>
                        공개 여부
                    </th>
                </tr>
                {listItems}
            </table>
        </div>
    </>);
}
