import React, {useEffect, useState} from "react";
import axios from "axios";

export default function DevelopProduct({employee}) {

    const [name, setName] = useState('');
    const [premium, setPremium] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();
        const productData = {
            name: name,
            premium: premium,
        };
        await axios.post('http://localhost:40055/api/product/create', productData)
            .then(response => {
                if (response.data === '')
                    alert('상품 등록에 실패했습니다.');
                else {
                    alert('상품이 등록되었습니다.');
                }
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    };

    if (employee.department !== "DEVELOPMENT") {
        alert("개발팀 직원만 접근 가능한 메뉴입니다.")
        return null;
    } else {
        return (
            <>
                <form onSubmit={handleSubmit}>
                    <div className="form">
                        <label htmlFor="name">상품 이름</label>
                        <input type="text" id="name" name="name" value={name}
                               onChange={(e) => setName(e.target.value)} required/>
                    </div>
                    <div className="form">
                        <label htmlFor="premium">보험료</label>
                        <input type="text" id="premium" name="premium" value={premium}
                               onChange={(e) => setPremium(e.target.value)} required/>
                    </div>
                    <button type="submit">제출</button>
                </form>
            </>
        );
    }

}
