import React, {useState} from 'react';
import './Login.css';
import axios from "axios";

export default function Login({setAuthenticated, setEmployee}) {

    const [userId, setUserId] = useState('');
    const [userPw, setUserPw] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        axios.get('http://localhost:40045/api/employee/login', {
            params: {
                id: userId,
                password: userPw,
            }
        })
            .then(response => {
                if (response.data === '')
                    alert('아이디 및 비밀번호를 다시 확인해주세요.');
                else {
                    alert('로그인에 성공했습니다.');
                    setEmployee({
                        "id": response.data.id,
                        "name": response.data.name,
                        "gender": response.data.gender,
                        "department": response.data.department
                    });
                    setAuthenticated(true);
                }
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    };

    return (
        <>
            <body>
            <div className="login-space">
                <h2>우리보험사 로그인</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form">
                        <label htmlFor="id">아이디</label>
                        <input type="text" id="id" name="id" value={userId}
                               onChange={(e) => setUserId(e.target.value)} required/>
                    </div>
                    <div className="form">
                        <label htmlFor="password">비밀번호</label>
                        <input type="password" id="password" name="password" value={userPw}
                               onChange={(e) => setUserPw(e.target.value)} required/>
                    </div>
                    <button type="submit">로그인</button>
                </form>
            </div>
            </body>
        </>
    );
}
