import React, {useEffect, useState} from "react";
import axios from "axios";
import './CreateAnswer.css'
import {ENDPOINT_GET_BOARDS, ENDPOINT_POST_ANSWER_BOARD} from "../../../../common/Endpoint/Endpoint";
import swal from "sweetalert";

function BoardRecord({board, setBoardId}) {
    return (
        <tr>
            <td>{board.title}</td>
            <td>{board.author}</td>
            <td>{board.content}</td>
            <button onClick={e => answerBoard()}>답변하기</button>
        </tr>
    );
}

export default function CreateAnswer({employee}) {

    const [boards, setBoards] = useState(null);

    function refreshBoards() {
        axios.get(ENDPOINT_GET_BOARDS)
            .then(response => {
                setBoards(response.data);
            })
            .catch(error => {
                swal({
                    title: '시스템 오류',
                    text: '잠시후 다시 시도해주세요. 불편을 드려 죄송합니다.',
                    icon: 'error',
                    button: '확인',
                });
            });
    }

    useEffect(() => {
        refreshBoards();
    }, []);

    function answerBoard({board}) {
        let answer;
        swal({
            content: "input",
        }).then(r => answer = r);
        axios.post(ENDPOINT_POST_ANSWER_BOARD, {
            id: board.id,
            answer: answer,
            answerer: employee.id
        })
            .then(response => {
                swal({
                    title: '답변 등록 성공',
                    text: '답변이 등록되었습니다.',
                    icon: 'success',
                    button: '확인',
                });
            })
            .catch(error => {
                swal({
                    title: '시스템 오류',
                    text: '잠시후 다시 시도해주세요. 불편을 드려 죄송합니다.',
                    icon: 'error',
                    button: '확인',
                });
            });
        refreshBoards();
    }

    return (
        <div className={'create_answer'}>
            <h2>문의 게시판</h2>
            <table>
                <tr>
                    <th>제목</th>
                    <th>작성자</th>
                    <th>내용</th>
                    <th>답변 버튼</th>
                </tr>
                {boards !== null ? boards.filter(board => board.is_answered === 0).map(board => <BoardRecord
                        board={board}/>)
                    : "LOADING..."
                }
            </table>
        </div>
    );
}
