import React, {useEffect, useState} from "react";
import axios from "axios";
import {ENDPOINT_GET_PRODUCTS} from "../../../../common/Endpoint/Endpoint";
import TextInput from "../../../../common/TextInput/TextInput";
import Button from "../../../../common/Button/Button";
import SelectInput from "../../../../common/SelectInput/SelectInput";
import swal from "sweetalert";

export default function Underwrite({employee}) {

    const [products, setProducts] = useState(null);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [seniorRate, setSeniorRate] = useState('');
    const [maleRate, setMaleRate] = useState('');
    const [femaleRate, setFemaleRate] = useState('');
    const [occupationalHazardRate, setOccupationalHazardRate] = useState('');
    const [smokingRate, setSmokingRate] = useState('');

    useEffect(() => {
        axios.get(ENDPOINT_GET_PRODUCTS)
            .then(response => {
                setProducts(response.data);
                setSelectedProduct(response.data[0]);
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const productData = {
            id: selectedProduct,
            senior_rate: seniorRate,
            male_rate: maleRate,
            female_rate: femaleRate,
            occupational_hazard_rate: occupationalHazardRate,
            smoking_rate: smokingRate,
        };
        console.log(productData);
        await axios.post('http://localhost:40055/api/product/update', productData)
            .then(response => {
                if (response.data === false)
                    alert('요율 결정에 실패했습니다.');
                else {
                    alert('요율이 결정되어 상품이 공개 상태로 전환되었습니다.');
                }
            })
            .catch(error => {
                alert('시스템이 응답하지 않습니다, 잠시후 다시 시도해주세요.');
            });
    };

    if (employee.department !== "UNDERWRITING") {
        swal({
            title: '권한 없음',
            text: '언더라이팅팀만 이용 가능한 메뉴입니다.',
            icon: 'error',
            button: '확인',
        });
        return null;
    }

    return (
        <form onSubmit={handleSubmit}>
            <SelectInput text={'상품 이름'} values={contracts.map(function (contract) {
                return contract.id
            })} onChange={(e) => setSelectedProduct(e.target.value)}/>
            <TextInput text={'고령 요율'} value={seniorRate} onChange={(e) => setSeniorRate(e.target.value)}/>
            <TextInput text={'남성 요율'} value={maleRate} onChange={(e) => setMaleRate(e.target.value)}/>
            <TextInput text={'여성 요율'} value={femaleRate} onChange={(e) => setFemaleRate(e.target.value)}/>
            <TextInput text={'위험 직업 요율'} value={occupationalHazardRate}
                       onChange={(e) => setOccupationalHazardRate(e.target.value)}/>
            <TextInput text={'흡연 요율'} value={smokingRate} onChange={(e) => setSmokingRate(e.target.value)}/>
            <Button text={'제출'}/>
        </form>
    );
}
