import React, {useEffect, useState} from "react";
import axios from "axios";
import './CreateAnswer.css'
import {ENDPOINT_GET_BOARDS, ENDPOINT_POST_ANSWER_BOARD} from "../../../../common/Endpoint/Endpoint";
import swal from "sweetalert";

function BoardRecord({board, setBoardId}) {
    return (
        <tr>
            <td>{board.title}</td>
            <td>{board.author}</td>
            <td>{board.content}</td>
            <button onClick={e => setBoardId(board.id)}>답변하기</button>
        </tr>
    );
}

export default function CreateAnswer({employee}) {

    const [boards, setBoards] = useState(null);

    function refreshBoards() {
        axios.get(ENDPOINT_GET_BOARDS)
            .then(response => {
                setBoards(response.data);
            })
            .catch(error => {
                swal({
                    title: '시스템 오류',
                    text: '잠시후 다시 시도해주세요. 불편을 드려 죄송합니다.',
                    icon: 'error',
                    button: '확인',
                });
            });
    }

    useEffect(() => {
        refreshBoards();
    }, []);

    function handleSubmit(e) {
        e.preventDefault();
        const boardData = {
            id: boardId,
            answer: content,
            answerer: employee.id
        };
        axios.post(ENDPOINT_POST_ANSWER_BOARD, boardData)
            .then(response => {
                swal({
                    title: '답변 등록 성공',
                    text: '답변이 등록되었습니다.',
                    icon: 'success',
                    button: '확인',
                });
            })
            .catch(error => {
                swal({
                    title: '시스템 오류',
                    text: '잠시후 다시 시도해주세요. 불편을 드려 죄송합니다.',
                    icon: 'error',
                    button: '확인',
                });
            });
        refreshBoards();
    }

    let listItems = null;
    if (boards !== null) {
        listItems = boards.filter(board =>
            board.answerer === ''
        ).map(board => <BoardRecord board={board}/>);
    } else listItems = "loading...";
    
    return (
        <div className={'create_answer'}>
            <h1>문의 게시판</h1>
            <table>
                <tr>
                    <th>제목</th>
                    <th>작성자</th>
                    <th>내용</th>
                    <th>답변 버튼</th>
                </tr>
                {listItems}
            </table>
        </div>
    );
}
